mapper.select:
SELECT B.* FROM( SELECT ROWNUM RNUM, A.* FROM ( SELECT bp_id , bp_title , bp_reg_dt , bp_cnt , bp_ntc_id , COMMON.CM_NAME as bp_ntc_name FROM board_post JOIN COMMON ON (BOARD_POST.BP_NTC_ID=COMMON.CM_ID) WHERE BRD_ID = 'BN0001' and bp_ntc_id ='BN0004' AND bp_del_dt IS NULL ORDER BY BP_REG_DT DESC ) A
) B
where rnum <=3

SELECT B.* FROM( SELECT ROWNUM RNUM, A.* FROM ( SELECT board_post.bp_id , EMP.emp_name , bp_title , bp_cn , bp_reg_dt , bp_cnt , BOARD_POST_FILE.BP_UUID_NAME FROM board_post JOIN EMP ON(board_post.emp_Id = emp.emp_Id) JOIN BOARD_POST_FILE ON (board_post.BP_ID=BOARD_POST_FILE.BP_ID) WHERE BRD_ID ='BN0004' AND bp_del_dt IS NULL AND BP_MAIN_IMG_YN = 'Y'
ORDER BY BP_REG_DT DESC )A )B
RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.update:
UPDATE
	    BOARD_POST
	SET
	    BP_TITLE = #{vo.bpTitle}
	    , BP_MOD_ID = #{vo.bpModId}
	    , BP_MOD_DT = #{vo.bpModDt}
	    , BP_CN = #{vo.bpCn}
	WHERE
	    BOARD_POST.BRD_ID = 'BN0004' AND BOARD_POST.BP_ID = #{vo.bpId}

mapper.insert:

SELECT 
	'F' || 
	LPAD(
	    NVL(
	        TO_NUMBER(
	            SUBSTR(
	            MAX(BP_FILE_ID), 2)
	            ), 0) + 1, 5, '0')
				FROM BOARD_POST_FILE

INSERT INTO board_post_file (
    bp_file_id
    , bp_id
    , bp_file_gb_id
    , bp_uuid_name
    , bp_org_nm
    , bp_main_img_yn
    , bp_file_size
) VALUES (
    #{bpFileId}
  , #{bpId}
  , #{bpFileGbId}
  , #{bpUuidName}
  , #{bpOrgNm}
  , #{bpMainImgYn}
  , #{bpFileSize}
)


	SELECT 
    'B'||LPAD(NVL(TO_NUMBER(SUBSTR(MAX(BC_ID),5)),0)+1,5,'0') 
    FROM BOARD_COMMENT

INSERT INTO BOARD_COMMENT (
	BC_ID
	, BC_TOP_ID
	, BP_ID
	, EMP_ID
	, BC_REG_DT
	, BC_CN
	, BC_DEL_YN
) VALUES (
#{bcId}
, #{bcTopId}
, #{bpId}
, #{empId}
, SYSTIMESTAMP
, #{bcCn,jdbcType=CLOB}
, 'N'
)
